import axios from "axios"
import * as types from './actionType'


export const AuthLoginAction = (email, password) => async (dispatch) => {
    try{
        const response = await axios.post(`https://online-books-app.herokuapp.com/api/users/login`, {
            email: email,
            password: password
          })
        console.log(response.data.user.token)
        if(response.data.success){
            localStorage.setItem('token', response.data.user.token);
            dispatch({
                type: types.AUTH_LOGGED_IN,
                payload: {
                    token: response.data.user.token,
                    userData: response.data.user
                }
            })

        }
    }
    catch(e){
        dispatch({
            type: types.AUTH_LOGGED_IN_ERROR,
            payload: response.data,
        })
        console.log(e)
    }
}


export const AuthLogoutAction = () => async (dispatch) => {
            localStorage.removeItem('token')
            dispatch({
                type: types.AUTH_LOGGED_OUT,
                payload: {
                    token: '',
                    userData: ''
                }
            }) 
}
