// @mui material components
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";

// Material Dashboard 2 React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import DataTable from "examples/Tables/DataTable";

// Data
import servicesData from "layouts/services/data/servicesData";
import { useEffect, useState } from "react";
import * as actions from "../../store/Actions/index";
import { connect } from "react-redux";
import MDAlert from "components/MDAlert";
import EditServiceModal from "components/EditServiceModal";
import MDButton from "components/MDButton";

function Services() {
  const {
    columns,
    rows,
    isModalVisible,
    setIsModalVisible,
    selectedItem,
    onSubmit,
    isLoading,
  } = servicesData();

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <MDBox pt={6} pb={3}>
        <Grid container spacing={6}>
          <Grid item xs={12}>
            <Card>
              <MDBox
                mx={2}
                mt={-3}
                py={3}
                px={2}
                variant="gradient"
                bgColor="info"
                borderRadius="lg"
                coloredShadow="info"
              >
                <MDTypography variant="h6" color="white">
                  Add Service
                </MDTypography>
                <MDBox
                  onClick={() => {
                    // setIsLoading(true);
                    // _onClickAction(ele);
                  }}
                >
                  <MDButton variant="gradient" color="secondary">
                    {ele?.services_status === 1
                      ? "Deactivate Now"
                      : "Activate Now"}
                  </MDButton>
                </MDBox>
              </MDBox>
              <MDBox pt={3}>
                {/* <MDAlert mount={true} color="primary" dismissible={true}> 
              <MDTypography variant="h6" color="white">
                  Services Provided
                </MDTypography>
              </MDAlert> */}
                {rows?.length > 0 && (
                  <DataTable
                    table={{ columns, rows }}
                    isSorted={false}
                    entriesPerPage={false}
                    showTotalEntries={false}
                    noEndBorder
                  />
                )}
              </MDBox>
            </Card>
          </Grid>
        </Grid>
        {isModalVisible && (
          <EditServiceModal
            isModalVisible={isModalVisible}
            setIsModalVisible={setIsModalVisible}
            selectedItem={selectedItem}
            isLoading={isLoading}
            onSubmit={onSubmit}
          />
        )}
      </MDBox>
      <Footer />
    </DashboardLayout>
  );
}

const mapStateToProps = ({ userAuthReducer }) => {
  return { userAuthReducer };
};
export default connect(mapStateToProps, actions)(Services);
